INSERT INTO `course_thread` (`id`, `courseId`, `lessonId`, `userId`, `type`, `isStick`, `isElite`, `isClosed`, `private`, `title`, `content`, `postNum`, `hitNum`, `followNum`, `latestPostUserId`, `latestPostTime`, `createdTime`, `updatedTime`) VALUES (1,2,0,1,'discussion',0,0,0,0,'各位同学，最近有没有什么学习活动啊？','<p>刚刚接触这个网站，看大家都很活跃，聊得很开心，最近有没有什么活动啊，想快点儿融入大家。</p>',0,1,0,1,1432803828,1432803828,1432803828),(2,2,0,1,'question',0,0,0,0,'学员可以在网校做哪些操作呢？','<p>有什么学习功能？</p>',0,1,0,1,1432803977,1432803977,1432803977),(3,4,43,1,'question',0,0,0,0,'我可以给网校添加用户吗？','<p>终于建好了网站，学员有地方学习啦~   如何能把学员拉到这个网校呢？</p>',0,0,0,1,1432890143,1432890143,1432890143),(4,6,63,9,'question',0,0,0,0,'为什么看视频会卡？','<p>因为你现在使用的是本地视频，所以受限于你资金的服务器带宽，如现在播放的高清视频需要你这提供4M的带宽。</p>\n\n<p>如果流程的体验，请购买我们的教育云。</p>',0,5,0,9,1433229565,1433229565,1433229565),(5,6,0,1,'discussion',0,0,0,0,'班级和课程的区别是什么？','<p><span style=\"color:rgb(0,0,0);\">班级和课程相比较，班级是体系化结构化的课程，而且除了课程还引入了交流互动的其他功能和元素。</span></p>',0,1,0,1,1433992295,1433992295,1433992295),(6,1,4,1,'question',0,0,0,0,'创建的课程不可以删除吗？','<p><span style=\"color:rgb(102,102,102);\">目前在系统中，已发布的课程不能删除，未发布的课程可以删除。我们知道此设置带来的不便，这是班级功能发布之后出于系统的稳定性考虑做的决定，后期可能会进行调整。</span></p>',0,0,0,1,1434009775,1434009775,1434009775);
